INSERT INTO `habito` VALUES (1,'Separação de Lixo Reciclável','Realizar a segregação correta de materiais recicláveis como papel, plástico, vidro e metal em casa ou no trabalho.',10),(2,'Coleta de Lixo Orgânico','Compostar resíduos orgânicos ou participar de programas de coleta seletiva de orgânicos para reduzir o descarte em aterros.',50),(3,'Descarte de Lixo Eletrônico','Levar eletrônicos antigos, pilhas e baterias a pontos de coleta específicos para descarte e reciclagem adequados.',100),(4,'Economia de Água','Adotar práticas para reduzir o consumo de água, como banhos mais curtos, conserto de vazamentos e reuso de água quando possível.',20),(5,'Uso de Transporte Sustentável','Optar por meios de transporte de baixo impacto ambiental, como bicicleta, caminhada ou transporte público, em vez de veículos poluentes.',30),(6,'Participação em Mutirão de Limpeza','Contribuir ativamente em ações comunitárias de limpeza de praias, rios, parques ou espaços públicos.',150),(7,'Compra de um Produto Sustentável/Local','Escolher produtos de empresas com práticas sustentáveis ou de produtores locais, apoiando a economia circular e reduzindo a pegada de carbono.',70);
